export function getStoreLogo(storeName: string): string {
  const store = storeName.toLowerCase();
  
  if (store.includes('amazon')) {
    return '/amazon-logo.png';
  }
  
  if (store.includes('mercado') || store.includes('livre') || store.includes('ml')) {
    return '/mercadolivre-logo.png';
  }
  
  if (store.includes('magazine') || store.includes('luiza') || store.includes('magalu')) {
    return '/magalu-logo.png';
  }
  
  if (store.includes('aliexpress')) {
    return '/aliexpress-logo.png';
  }
  
  if (store.includes('americanas')) {
    return 'data:image/svg+xml;base64,';
  }
  
  if (store.includes('shopee')) {
    return 'data:image/svg+xml;base64,';
  }
  
  if (store.includes('casas') || store.includes('bahia')) {
    return 'data:image/svg+xml;base64,';
  }
  
  if (store.includes('extra')) {
    return 'data:image/svg+xml;base64,';
  }
  
  if (store.includes('shoptime')) {
    return 'data:image/svg+xml;base64,';
  }
  
  if (store.includes('submarino')) {
    return 'data:image/svg+xml;base64,';
  }
  
  // Logo gen√©rico para outras lojas
  return 'data:image/svg+xml;base64,';
}